import React, { Component } from 'react';
import './App.css';

class App extends Component {
    handleInput = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
        this.setState({
            [name]: value
        });
    };
  render() {
    return (
      <div className="App">
        Username:
        <input
            type="text"
            name="Username"
            onChange={this.handleInput}/>
        Email:
        <input
            type="text"
            name="Email"
            onChange={this.handleInput}/>
        <div>
          <input
              type="checkbox"
              name="Agreed"
              onChange={this.handleInput}/>I agree to the terms
        </div>
        <button className="btn btn-outline-info">Sign up</button>
      </div>
    );
  }
}

export default App;
