import React, { Component } from 'react';
import './App.css';

class App extends Component {
    state ={
      errorValidation:{
          Username: "Username must be filled",
          Email: "Email must be filled",
          Agree: "Agree must be checked",
      }

    };
    handleInput = (e) => {
        const inputs = {...this.state.inputs};
        const target = e.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
        inputs[name] = value;
        this.setState({
            inputs
        });
    };
    handleSubmit = (e) => {
        alert(this.state.inputs);
        e.preventDefault();
    };
  render() {
    return (
      <div className="App">
        <form className='App' onSubmit={this.handleSubmit}>
          Username:
          <input
              type="text"
              name="Username"
              onChange={this.handleInput}/>
          Email:
          <input
              type="text"
              name="Email"
              onChange={this.handleInput}/>
          <div>
            <input
                type="checkbox"
                name="Agreed"
                onChange={this.handleInput}/>I agree to the terms
          </div>
          <button
              type="submit"
              className="btn btn-outline-info"
              >Sign up</button>
        </form>
      </div>
    );
  }
}

export default App;
