import React, { Component } from 'react';
import './App.css';

class App extends Component {
    handleInput = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.setState({
            [name]: value
        });
    };
  render() {
    return (
      <div className="App">
        <input
            type="text"
            name="userInput"
            onChange={this.handleInput}/>
      </div>
    );
  }
}

export default App;
