import React, { Component } from 'react';
import './App.css';
import Username from "./components/Username";
import Email from "./components/Email";
import Agree from "./components/Agree";

class App extends Component {
    state ={
      errorValidation:{
          Username: ["Username must be filled",true],
          Email: ["Email must be filled",true],
          Agree: ["Agree must be checked",true],
      }

    };
    handleInput = (e) => {
        const inputs = {...this.state.inputs};
        const target = e.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
        inputs[name] = value;
        this.setState({
            inputs
        });
    };
    handleSubmit = (e) => {
        this.validateInputs();
        alert(JSON.stringify(this.state.inputs));
        e.preventDefault();
    };
    validateInputs = () => {
      let errors = {...this.state.errorValidation};
        Object.keys(errors).map((key) =>{
          if(this.state.inputs!==undefined) {
              if (key ==='Agree' && this.state.inputs[key]===false){
                errors[key][1]= false;
                  this.setState({
                      errors
                  })
              }
              else if (this.state.inputs[key].length < 0) {
                  errors[key][1]= false;
                  this.setState({
                      errorValidation:errors
                  })
              }
              console.log(errors[key])
          }
        })
    };
  render() {
    return (
      <div className="App">
        <form className='App' onSubmit={this.handleSubmit}>
          <Username
              handleInput={this.handleInput}
              error={this.state.errorValidation.Username}/>
          <Email
              handleInput={this.handleInput}
              error={this.state.errorValidation.Email}/>
          <Agree
              handleInput={this.handleInput}
              error={this.state.errorValidation.Agree}/>
          <button
              type="submit"
              className="btn btn-outline-info"
              >Sign up</button>
        </form>
      </div>
    );
  }
}

export default App;
